import React, { useEffect, useState } from 'react';

function App() {
  const [joke, setJoke] = useState("");

  const fetchJoke = async () => {
    const response = await fetch('http://localhost:3001/api/joke');
    const data = await response.json();
    setJoke(data.joke);
  };

  useEffect(() => {
    fetchJoke();
  }, []);

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px' }}>
      {/* Typeface Assignment Header */}
      <div style={{ backgroundColor: '#f4f4f4', padding: '15px', borderRadius: '10px', marginBottom: '20px', border: '1px solid #ccc' }}>
        <h2 style={{ margin: '0 0 5px 0', color: '#d32f2f' }}>Typeface</h2>
        <p style={{ margin: 0, fontSize: '14px', color: '#555' }}>DevOps Engineer | Project Assignment</p>
      </div>

      {/* Main Joke App */}
      <h1>Random Joke Generator</h1>
      <p style={{ fontSize: '18px' }}>{joke}</p>
      <button onClick={fetchJoke} style={{ marginTop: '10px', padding: '10px 20px' }}>
        Get Another Joke
      </button>
    </div>
  );
}

export default App;
